import React from "react";
import { PieChart } from "react-minimal-pie-chart";

class Calculator extends React.Component {
  state = {
    currency: "CAD",
    prev_currency: "CAD",
    defaultLabelStyle: {
      fontSize: "2px",
      fontFamily: "sans-serif",
      fill: "white"
    },
    isEditing: false,
    cur_codes: {
      CAD: <span>&#36;</span>,
      USD: <span>&#36;</span>,
      NGN: <span>&#8358;</span>,
      EUR: <span>&euro;</span>,
      INR: <span>&#8377;</span>,
      GBP: <span>&pound;</span>,
      KRW: <span>&#8360;</span>,
      PHP: <span>&#8369;</span>,
      AED: <span>&#x62f;&#x2e;&#x625;</span>,
      JPY: <span>&#165;</span>
    },
    values: {
      chequing: 2000,
      savings_for_taxes: 4000.0,
      rainy_day_fund: 506.0,
      savings_for_fun: 5000.0,
      savings_for_travel: 400.0,
      savings_for_personal_development: 200.0,
      investment_1: 5000.0,
      investment_2: 60000.0,
      investment_3: 24000.0,
      primary_home: 455000.0,
      second_home: 1564321.0,
      other: 2000.0,
      credit_card_1: 4342.0,
      credit_card_2: 322.0,
      mortgage_1: 250999.0,
      mortgage_2: 632634.0,
      line_of_credit: 10000.0,
      investment_loan: 10000.0
    },
    total_assets: 2122427.0,
    total_liabilities: 908297.0,
    net_worth: 1214130.0
  };

  updateInputValue = async e => {
    //gets called when an amount is edited and calls the server to get the updated totals
    let values = this.state.values;
    values[e.target.name] = e.target.value === "" ? 0 : e.target.value;
    await this.setState({ values: values });
    fetch("/calculator/", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify(this.state)
    })
      .then(res => {
        if (res.ok) {
          return res.json();
        }
      })
      .then(data => {
        this.setState({
          total_assets: data.total_assets,
          total_liabilities: data.total_liabilities,
          net_worth: data.net_worth
        });
      });
  };

  convertCurrency = async e => {
    //gets called when an amount is edited and calls the server to get the updated totals
    var fromCurrency = encodeURIComponent(this.state.prev_currency);
    var toCurrency = encodeURIComponent(e.target.value);
    fetch("/convert-currency/", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        values: this.state.values,
        fromCurrency: fromCurrency,
        toCurrency: toCurrency,
        total_assets: this.state.total_assets,
        total_liabilities: this.state.total_liabilities,
        net_worth: this.state.net_worth
      })
    })
      .then(res => {
        if (res.ok) {
          return res.json();
        }
      })
      .then(data => {
        this.setState({
          values: data.updated_values,
          currency: toCurrency,
          prev_currency: toCurrency,
          total_assets: data.newTotalAssets,
          total_liabilities: data.newTotalLiabilities,
          net_worth: data.newNetWorth
        });
      });
  };

  toggleEditing() {
    // used for formatting the input elements for the amount
    this.setState({ isEditing: !this.state.isEditing });
  }

  formatToCurrency(number) {
    // changes the format of the amount to currency format
    return number.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  }

  render() {
    return (
      <div className="App">
        <div className="left">
          <h1 id="title">
            <span id="net" className="display-1">
              Net
            </span>
            <br></br>
            <span id="worth" className="display-1">
              Worth
            </span>
            <br></br>
            <span id="calc" className="display-1">
              Calculator
            </span>
          </h1>
          <br></br>
          <br></br>
          <table className="table">
            <tbody>
              <tr>
                <td className="blue">&nbsp;</td>
                <td>Total Assets:</td>
                <td>
                  <span>{this.state.cur_codes[this.state.currency]}</span>
                  {this.formatToCurrency(this.state.total_assets)}
                </td>
              </tr>
              <tr>
                <td className="orange">&nbsp;</td>
                <td>Total Liabilities:</td>
                <td>
                  <span>{this.state.cur_codes[this.state.currency]}</span>
                  {this.formatToCurrency(this.state.total_liabilities)}
                </td>
              </tr>
              <tr>
                <td></td>
                <td>Net Worth:</td>
                <td>
                  <span>{this.state.cur_codes[this.state.currency]}</span>
                  {this.formatToCurrency(this.state.net_worth)}
                </td>
              </tr>
            </tbody>
          </table>
          <PieChart
            label={({ dataEntry }) => `${Math.round(dataEntry.percentage)} %`}
            animation
            animationDuration={500}
            animationEasing="ease-out"
            center={[50, 50]}
            data={[
              {
                title: "Assets",
                value: parseFloat(this.state.total_assets),
                color: "#0066CC"
              },
              {
                title: "Liabilities",
                value: parseFloat(this.state.total_liabilities),
                color: "#FFA500"
              }
            ]}
            labelPosition={90}
            labelStyle={this.state.defaultLabelStyle}
            // lengthAngle={360}
            lineWidth={25}
            paddingAngle={0}
            radius={45}
            viewBoxSize={[200, 200]}
          />
          }
        </div>
        <div className="right">
          <div id="currency-select">
            <label htmlFor="currencies">Currency: </label>
            <select
              id="currencies"
              name="currencies"
              className="form-select form-select-sm"
              aria-label=".form-select-sm example"
              onChange={this.convertCurrency}
            >
              {Object.entries(this.state.cur_codes).map(([key, value]) => (
                <option value={key} key={key}>
                  {key}
                </option>
              ))}
            </select>
          </div>
          <br></br>
          <br></br>
          <h4>Assets</h4>
          <table className="table">
            <tbody>
              <tr>
                <th>Cash and Investments</th>
                <th></th>
                <th></th>
              </tr>
              <tr>
                <td>Chequing</td>
                <td></td>
                <td>
                  <div className="input-group input-group-sm">
                    <span
                      className="input-group-text"
                      id="inputGroup-sizing-sm"
                    >
                      {this.state.cur_codes[this.state.currency]}
                    </span>
                    {this.state.isEditing ? (
                      <input
                        type="number"
                        className="form-control"
                        name="chequing"
                        value={
                          Math.round(this.state.values["chequing"] / 100) * 100
                        }
                        onChange={this.updateInputValue}
                        onBlur={this.toggleEditing.bind(this)}
                      ></input>
                    ) : (
                      <input
                        type="text"
                        className="form-control"
                        name="chequing"
                        value={this.formatToCurrency(
                          Math.round(this.state.values["chequing"] / 100) * 100
                        )}
                        onFocus={this.toggleEditing.bind(this)}
                        readOnly
                      ></input>
                    )}
                  </div>
                </td>
              </tr>
              <tr>
                <td>Savings for Taxes</td>
                <td></td>
                <td>
                  <div className="input-group input-group-sm">
                    <span
                      className="input-group-text"
                      id="inputGroup-sizing-sm"
                    >
                      {this.state.cur_codes[this.state.currency]}
                    </span>
                    {this.state.isEditing ? (
                      <input
                        type="number"
                        className="form-control"
                        name="savings_for_taxes"
                        value={
                          Math.round(
                            this.state.values["savings_for_taxes"] / 100
                          ) * 100
                        }
                        onChange={this.updateInputValue}
                        onBlur={this.toggleEditing.bind(this)}
                      ></input>
                    ) : (
                      <input
                        type="text"
                        className="form-control"
                        name="savings_for_taxes"
                        value={this.formatToCurrency(
                          Math.round(
                            this.state.values["savings_for_taxes"] / 100
                          ) * 100
                        )}
                        onFocus={this.toggleEditing.bind(this)}
                        readOnly
                      ></input>
                    )}
                  </div>
                </td>
              </tr>
              <tr>
                <td>Rainy Day Fund</td>
                <td></td>
                <td>
                  <div className="input-group input-group-sm">
                    <span
                      className="input-group-text"
                      id="inputGroup-sizing-sm"
                    >
                      {this.state.cur_codes[this.state.currency]}
                    </span>
                    {this.state.isEditing ? (
                      <input
                        type="number"
                        className="form-control"
                        name="rainy_day_fund"
                        value={
                          Math.round(
                            this.state.values["rainy_day_fund"] / 100
                          ) * 100
                        }
                        onChange={this.updateInputValue}
                        onBlur={this.toggleEditing.bind(this)}
                      ></input>
                    ) : (
                      <input
                        type="text"
                        className="form-control"
                        name="rainy_day_fund"
                        value={this.formatToCurrency(
                          Math.round(
                            this.state.values["rainy_day_fund"] / 100
                          ) * 100
                        )}
                        onFocus={this.toggleEditing.bind(this)}
                        readOnly
                      ></input>
                    )}
                  </div>
                </td>
              </tr>
              <tr>
                <td>Savings for Fun</td>
                <td></td>
                <td>
                  <div className="input-group input-group-sm">
                    <span
                      className="input-group-text"
                      id="inputGroup-sizing-sm"
                    >
                      {this.state.cur_codes[this.state.currency]}
                    </span>
                    {this.state.isEditing ? (
                      <input
                        type="number"
                        className="form-control"
                        name="savings_for_fun"
                        value={
                          Math.round(
                            this.state.values["savings_for_fun"] / 100
                          ) * 100
                        }
                        onChange={this.updateInputValue}
                        onBlur={this.toggleEditing.bind(this)}
                      ></input>
                    ) : (
                      <input
                        type="text"
                        className="form-control"
                        name="savings_for_fun"
                        value={this.formatToCurrency(
                          Math.round(
                            this.state.values["savings_for_fun"] / 100
                          ) * 100
                        )}
                        onFocus={this.toggleEditing.bind(this)}
                        readOnly
                      ></input>
                    )}
                  </div>
                </td>
              </tr>
              <tr>
                <td>Savings for Travel</td>
                <td></td>
                <td>
                  <div className="input-group input-group-sm">
                    <span
                      className="input-group-text"
                      id="inputGroup-sizing-sm"
                    >
                      {this.state.cur_codes[this.state.currency]}
                    </span>
                    {this.state.isEditing ? (
                      <input
                        type="number"
                        className="form-control"
                        name="savings_for_travel"
                        value={
                          Math.round(
                            this.state.values["savings_for_travel"] / 100
                          ) * 100
                        }
                        onChange={this.updateInputValue}
                        onBlur={this.toggleEditing.bind(this)}
                      ></input>
                    ) : (
                      <input
                        type="text"
                        className="form-control"
                        name="savings_for_travel"
                        value={this.formatToCurrency(
                          Math.round(
                            this.state.values["savings_for_travel"] / 100
                          ) * 100
                        )}
                        onFocus={this.toggleEditing.bind(this)}
                        readOnly
                      ></input>
                    )}
                  </div>
                </td>
              </tr>
              <tr>
                <td>Savings for Personal Development</td>
                <td></td>
                <td>
                  <div className="input-group input-group-sm">
                    <span
                      className="input-group-text"
                      id="inputGroup-sizing-sm"
                    >
                      {this.state.cur_codes[this.state.currency]}
                    </span>
                    {this.state.isEditing ? (
                      <input
                        type="number"
                        className="form-control"
                        name="savings_for_personal_development"
                        value={
                          Math.round(
                            this.state.values[
                              "savings_for_personal_development"
                            ] / 100
                          ) * 100
                        }
                        onChange={this.updateInputValue}
                        onBlur={this.toggleEditing.bind(this)}
                      ></input>
                    ) : (
                      <input
                        type="text"
                        className="form-control"
                        name="savings_for_personal_development"
                        value={this.formatToCurrency(
                          Math.round(
                            this.state.values[
                              "savings_for_personal_development"
                            ] / 100
                          ) * 100
                        )}
                        onFocus={this.toggleEditing.bind(this)}
                        readOnly
                      ></input>
                    )}
                  </div>
                </td>
              </tr>
              <tr>
                <td>Investment 1</td>
                <td></td>
                <td>
                  <div className="input-group input-group-sm">
                    <span
                      className="input-group-text"
                      id="inputGroup-sizing-sm"
                    >
                      {this.state.cur_codes[this.state.currency]}
                    </span>
                    {this.state.isEditing ? (
                      <input
                        type="number"
                        className="form-control"
                        name="investment_1"
                        value={
                          Math.round(this.state.values["investment_1"] / 100) *
                          100
                        }
                        onChange={this.updateInputValue}
                        onBlur={this.toggleEditing.bind(this)}
                      ></input>
                    ) : (
                      <input
                        type="text"
                        className="form-control"
                        name="investment_1"
                        value={this.formatToCurrency(
                          Math.round(this.state.values["investment_1"] / 100) *
                            100
                        )}
                        onFocus={this.toggleEditing.bind(this)}
                        readOnly
                      ></input>
                    )}
                  </div>
                </td>
              </tr>
              <tr>
                <td>Investment 2</td>
                <td></td>
                <td>
                  <div className="input-group input-group-sm">
                    <span
                      className="input-group-text"
                      id="inputGroup-sizing-sm"
                    >
                      {this.state.cur_codes[this.state.currency]}
                    </span>
                    {this.state.isEditing ? (
                      <input
                        type="number"
                        className="form-control"
                        name="investment_2"
                        value={
                          Math.round(this.state.values["investment_2"] / 100) *
                          100
                        }
                        onChange={this.updateInputValue}
                        onBlur={this.toggleEditing.bind(this)}
                      ></input>
                    ) : (
                      <input
                        type="text"
                        className="form-control"
                        name="investment_2"
                        value={this.formatToCurrency(
                          Math.round(this.state.values["investment_2"] / 100) *
                            100
                        )}
                        onFocus={this.toggleEditing.bind(this)}
                        readOnly
                      ></input>
                    )}
                  </div>
                </td>
              </tr>
              <tr>
                <td>Investment 3</td>
                <td></td>
                <td>
                  <div className="input-group input-group-sm">
                    <span
                      className="input-group-text"
                      id="inputGroup-sizing-sm"
                    >
                      {this.state.cur_codes[this.state.currency]}
                    </span>
                    {this.state.isEditing ? (
                      <input
