import { useState } from "react";
import Title from "./components/Title";
import Networth from "./components/Networth";
import Currencyselect from "./components/Currencyselect";
import Assets from "./components/Assets";
import Liabilities from "./components/Liabilities";

function App() {
  const [titles, setTitles] = useState([
    { id: "net", text: "Net ", class_name: "display-1" },
    { id: "worth", text: "Worth ", class_name: "display-2" },
    { id: "calc", text: "Calculator", class_name: "display-3" }
  ]);
  const [networths, setNetworths] = useState([
    {
      id: "total_assets",
      value: 2122427.0,
      class: "blue",
      text: "Total Assets:"
    },
    {
      id: "total_liabilities",
      value: 908297.0,
      class: "orange",
      text: "Total Liabilities:"
    },
    { id: "net_worth", value: 1214130.0, class: "", text: "Net Worth:" }
  ]);
  const [currencyCodes, setCurrencyCodes] = useState([
    {
      CAD: <span>&#36;</span>,
      USD: <span>&#36;</span>,
      NGN: <span>&#8358;</span>,
      EUR: <span>&euro;</span>,
      INR: <span>&#8377;</span>,
      GBP: <span>&pound;</span>,
      KRW: <span>&#8360;</span>,
      PHP: <span>&#8369;</span>,
      AED: <span>&#x62f;&#x2e;&#x625;</span>,
      JPY: <span>&#165;</span>
    }
  ]);
  const [cashInvestments, setCashInvestments] = useState([
    {
      id: "chequing",
      value: 2000,
      text: "Chequing"
    },
    {
      id: "savings_for_taxes",
      value: 4000.0,
      text: "Savings for Taxes"
    },
    {
      id: "rainy_day_fund",
      value: 506.0,
      text: "Rainy Day Fund"
    },
    {
      id: "savings_for_fun",
      value: 5000.0,
      text: "Savings for Fun"
    },
    {
      id: "savings_for_travel",
      value: 400.0,
      text: "Savings for Travel"
    },
    {
      id: "savings_for_personal_development",
      value: 200.0,
      text: "Savings for Personal Development"
    },
    {
      id: "investment_1",
      value: 5000.0,
      text: "Investment 1"
    },
    {
      id: "investment_2",
      value: 60000.0,
      text: "Investment 2"
    },
    {
      id: "investment_3",
      value: 24000.0,
      text: "Investment 3"
    }
  ]);
  const [longTermAssets, setLongTermAssets] = useState([
    {
      id: "primary_home",
      value: 455000,
      text: "Primary Home"
    },
    {
      id: "second_home",
      value: 1564321.0,
      text: "Second Home"
    },
    {
      id: "other",
      value: 2000.0,
      text: "Other"
    }
  ]);
  const [shortTerm, setShortTerm] = useState([
    {
      id: "credit_card_1",
      value: 4342,
      text: "Credit Card 1"
    },
    {
      id: "credit_card_2",
      value: 322.0,
      text: "Credit Card 2"
    }
  ]);
  const [longTermDebt, setLongTermDebt] = useState([
    {
      id: "mortgage_1",
      value: 250999,
      text: "Mortgage 1"
    },
    {
      id: "mortgage_2",
      value: 632634.0,
      text: "Mortgage 2"
    },
    {
      id: "line_of_credit",
      value: 10000.0,
      text: "Line of Credit"
    },
    {
      id: "investment_loan",
      value: 10000.0,
      text: "Investment Loan"
    }
  ]);
  const [isEditing, setIsEditing] = useState([{ isEditing: false }]);
  const [currency, setCurrency] = useState([
    { currency: "CAD" },
    { prev_currency: "CAD" }
  ]);

  const updateInputValue = async e => {
    //gets called when an amount is edited and calls the server to get the updated totals
    let values = this.state.values;
    values[e.target.name] = e.target.value === "" ? 0 : e.target.value;
    await this.setState({ values: values });
    fetch("/calculator/", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify(this.state)
    })
      .then(res => {
        if (res.ok) {
          return res.json();
        }
      })
      .then(data => {
        this.setState({
          total_assets: data.total_assets,
          total_liabilities: data.total_liabilities,
          net_worth: data.net_worth
        });
      });
  };

  const convertCurrency = async e => {
    //gets called when an amount is edited and calls the server to get the updated totals
    var fromCurrency = encodeURIComponent(currency[1]["prev_currency"]);
    var toCurrency = encodeURIComponent(e.target.value);
    console.log({ values });
    fetch("/convert-cur/", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        cashInvestments: cashInvestments,
        longTermAssets: longTermAssets,
        shortTerm: shortTerm,
        longTermDebt: longTermDebt,
        fromCurrency: fromCurrency,
        toCurrency: toCurrency,
        networths: networths
      })
    })
      .then(res => {
        if (res.ok) {
          return res.json();
        }
      })
      .then(data => {
        // this.setState({
        //   values: data.updated_values,
        //   currency: toCurrency,
        //   prev_currency: toCurrency,
        //   total_assets: data.newTotalAssets,
        //   total_liabilities: data.newTotalLiabilities,
        //   net_worth: data.newNetWorth
        // });
        console.log(data);
      });
  };

  // format currency
  const formatToCurrency = number => {
    // changes the format of the amount to currency format
    return number.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  const toggleEditing = () => {
    // changes the format of the amount to currency format
    setIsEditing([{ isEditing: !isEditing[0].isEditing }]);
  };

  return (
    <div className="App">
      <div className="left">
        <Title titles={titles} />
        <Networth
          networths={networths}
          formatToCurrency={formatToCurrency}
          currency={currency}
          currency_codes={currencyCodes}
        />
      </div>
      <div className="right">
        <Currencyselect
          currencies={currencyCodes}
          convertCurrency={convertCurrency}
        />
        <br></br>
        <br></br>
        <h4>Assets</h4>
        <Assets
          cash_investments={cashInvestments}
          long_term_assets={longTermAssets}
          is_editing={isEditing}
          formatToCurrency={formatToCurrency}
          toggleEditing={toggleEditing}
          currency={currency}
          currency_codes={currencyCodes}
        />
        <br></br>
        <h4>Liabilities</h4>
        <Liabilities
          short_term={shortTerm}
          long_term_debt={longTermDebt}
          is_editing={isEditing}
          formatToCurrency={formatToCurrency}
          toggleEditing={toggleEditing}
          currency={currency}
          currency_codes={currencyCodes}
        />
      </div>
    </div>
  );
}

export default App;
