const Liability = ({
  liability,
  is_editing,
  formatToCurrency,
  toggleEditing,
  currency,
  currency_codes,
  updateInputValue
}) => {
  return (
    <tr>
      <td>{liability.text}</td>
      <td></td>
      <td>
        <div className="input-group input-group-sm">
          <span className="input-group-text" id="inputGroup-sizing-sm">
            {currency_codes[0][currency["currency"]]}
          </span>
          {is_editing[0].isEditing ? (
            <input
              type="number"
              className="form-control"
              name={liability.id}
              id={liability.name}
              value={Math.round(liability.value)}
              onChange={updateInputValue}
              onBlur={toggleEditing}
            ></input>
          ) : (
            <input
              type="text"
              className="form-control"
              name={liability.id}
              id={liability.name}
              value={formatToCurrency(Math.round(liability.value))}
              onFocus={toggleEditing}
              readOnly
            ></input>
          )}
        </div>
      </td>
    </tr>
  );
};

export default Liability;
